(function() {
  var Viz, analyser, canvas, context, masterGain, pluck, style, updateViz, viz;

  style = document.createElement('style');

  style.innerText = require("./style");

  document.body.appendChild(style);

  canvas = require("./filled_canvas");

  context = new AudioContext;

  Viz = require("./lib/viz");

  masterGain = context.createGain();

  masterGain.gain.value = 1;

  masterGain.connect(context.destination);

  analyser = context.createAnalyser();

  analyser.smoothingTimeConstant = 0;

  masterGain.connect(analyser);

  analyser.connect(context.destination);

  viz = Viz(analyser);

  updateViz = function() {
    viz.draw(canvas);
    return requestAnimationFrame(updateViz);
  };

  requestAnimationFrame(updateViz);

  pluck = function(context, frequency, impulseDuration) {
    var N, impulseSamples, n, node, y;
    if (frequency == null) {
      frequency = 220;
    }
    if (impulseDuration == null) {
      impulseDuration = 0.01;
    }
    impulseSamples = impulseDuration * context.sampleRate;
    node = context.createScriptProcessor(4096, 0, 1);
    N = Math.round(context.sampleRate / frequency);
    y = new Float32Array(N);
    n = 0;
    node.onaudioprocess = function(e) {
      var output;
      output = e.outputBuffer.getChannelData(0);
      return output.forEach(function(_, i) {
        var xn;
        impulseSamples -= 1;
        xn = impulseSamples >= 0 ? 2 * Math.random() - 1 : 0;
        y[n] = xn + (y[n] + y[(n + 1) % N]) / 2.004;
        output[i] = y[n];
        n += 1;
        if (n >= N) {
          return n = 0;
        }
      });
    };
    node.connect(analyser);
    return setTimeout(function() {
      return node.disconnect(analyser);
    }, 10000);
  };

  document.addEventListener("mousedown", function(e) {
    var frequency, impulseDuration, octaves;
    octaves = 3;
    frequency = (1 + octaves * e.pageX / document.body.clientWidth) * 220;
    impulseDuration = (1 - e.pageY / document.body.clientHeight) * 0.0075 + 0.0005;
    return pluck(context, frequency, impulseDuration);
  });

}).call(this);

