lib/viz.content:
(function() {
  module.exports = function(analyser) {
    var bins, frequencyDomain, timeDomain;
    bins = analyser.frequencyBinCount;
    frequencyDomain = new Uint8Array(bins);
    timeDomain = new Uint8Array(bins);
    return {
      draw: function(canvas) {
        var ctx, height, ratio, step, width;
        analyser.getByteFrequencyData(frequencyDomain);
        analyser.getByteTimeDomainData(timeDomain);
        width = canvas.width;
        height = canvas.height;
        ctx = canvas.getContext('2d');
        ratio = height / 256;
        step = width / bins;
        ctx.fillStyle = "black";
        ctx.fillRect(0, 0, width, height);
        (function() {
          ctx.fillStyle = "#0FF";
          ctx.beginPath();
          ctx.moveTo(0, height);
          Array.prototype.forEach.call(frequencyDomain, function(value, index) {
            var x, y;
            x = index * step;
            y = ratio * (256 - value);
            return ctx.lineTo(x, y);
          });
          ctx.lineTo(width, height);
          return ctx.fill();
        });
        ctx.lineWidth = 2;
        ctx.strokeStyle = "#FF0";
        Array.prototype.forEach.call(timeDomain, function(value, index) {
          var x, y;
          x = index * step;
          y = ratio * (256 - value);
          if (index === 0) {
            ctx.beginPath();
            return ctx.moveTo(x, y);
          } else {
            return ctx.lineTo(x, y);
          }
        });
        return ctx.stroke();
      }
    };
  };

}).call(this);

filled_canvas.content:
(function() {
  var canvas, handleResize;

  canvas = document.createElement('canvas');

  document.body.appendChild(canvas);

  handleResize = function() {
    canvas.width = window.innerWidth;
    return canvas.height = window.innerHeight;
  };

  handleResize();

  window.addEventListener("resize", handleResize, false);

  module.exports = canvas;

}).call(this);
