(function() {
  var context, pluck, style;

  context = new AudioContext;

  style = document.createElement('style');

  style.innerText = require("./style");

  document.body.appendChild(style);

  pluck = function(context, frequency, impulseDuration) {
    var N, impulseSamples, n, node, y;
    if (frequency == null) {
      frequency = 220;
    }
    if (impulseDuration == null) {
      impulseDuration = 0.01;
    }
    impulseSamples = impulseDuration * context.sampleRate;
    node = context.createScriptProcessor(4096, 0, 1);
    N = Math.round(context.sampleRate / frequency);
    y = new Float32Array(N);
    n = 0;
    node.onaudioprocess = function(e) {
      var output;
      output = e.outputBuffer.getChannelData(0);
      return output.forEach(function(_, i) {
        var xn;
        impulseSamples -= 1;
        xn = impulseSamples >= 0 ? 2 * Math.random() - 1 : 0;
        y[n] = xn + (y[n] + y[(n + 1) % N]) / 2.02;
        output[i] = y[n];
        n += 1;
        if (n >= N) {
          return n = 0;
        }
      });
    };
    node.connect(context.destination);
    return setTimeout(function() {
      return node.disconnect(context.destination);
    }, 10000);
  };

  document.addEventListener("mousedown", function(e) {
    var frequency, impulseDuration;
    frequency = (1 + e.pageX / document.body.clientWidth) * 220;
    impulseDuration = (1 - e.pageY / document.body.clientHeight) * 0.025;
    return pluck(context, frequency, impulseDuration);
  });

}).call(this);
